function removeElementsByClass(className){var elements=document.getElementsByClassName(className);if(window.innerWidth<768){for(var i=0;i<elements.length;i+=1){elements[i].parentNode.removeChild(elements[i])}}}window.addEventListener('load',function(){removeElementsByClass('js-banner-study-history')});window.addEventListener('resize',function(){removeElementsByClass('js-banner-study-history')});