$(function(){var breakpoint=window.matchMedia('(min-width:768px)');var courseSwiper;var breakpointChecker=function(){if(breakpoint.matches===true){if(courseSwiper!==undefined){courseSwiper.destroy(true,true)}return;}else if(breakpoint.matches===false){return enableSwiper()}};var enableSwiper=function(){$(".js-swiper-container--only-mobile").each(function(index,element){var swiperParent=$(this).parent(".col");var courseSwiper=new Swiper(element,{a11y:true,keyboardControl:true,slidesPerView:'auto',spaceBetween:10,observer:true,observerParents:true,scrollbar:{el:swiperParent.find('.swiper-scrollbar'),dragClass:'swiper-scrollbar-drag',dragSize:120,snapOnRelease:false},breakpoints:{450:{slidesPerView:'auto'},768:{slidesPerView:2}}})})};breakpoint.addListener(breakpointChecker);breakpointChecker();$('.js-course-select').click(function(){if($(this).prop('checked')==true){$(this).parents('.item').addClass('item--programme--is-selected');$(this).next('label').text('Course added')}else{$(this).parents('.item').removeClass('item--programme--is-selected');$(this).next('label').text('Add course')}});$('.item--programme').click(function(e){e.preventDefault()});$('.js-deactivate-on-click').click(function(){$(this).addClass('button--inactive').prop('disabled',true).text('Saved').find('span').remove()});$('.js-show-tooltip').click(function(){$(this).next('.js-progress-saved-tooltip').show()});$(document).mouseup(function(e){var container=$('.js-progress-saved-tooltip');if(!container.is(e.target)&&container.has(e.target).length===0){container.hide()}});$('.js-save-for-later').click(function(){$('.js-save-for-later').toggleClass('is-active').find('span:first-of-type').toggleClass('fas').toggleClass('fal')})});