$(function(){$('.js-enable-undo .item__favourite.active').on('keypress click',function(e){e.preventDefault();if(e.which===13||e.type==='click'){var title=$(this).parents('.item').find('h3').html();$(this).parents('.item').append('<div class="item item--undo"><div><div><h3>'+title+' has been removed from your Wishlist</h3><button class="js-undo-remove button button--outlined button--medium"><span class="fa-regular fa-undo"></span>Undo</button></div></div></div>');$(this).parents('.item').find('.js-undo-remove').focus();$('.js-undo-remove').click(function(){$(this).parents('.item--undo').detach()})}})});